let dots = document.querySelectorAll(".slider__control-button"), dotsArea = document.querySelector(".slider__controls"), slides = document.querySelectorAll(".slider__item"), slideIndex = 1; showSlides(slideIndex); function showSlides(a) { 1 > a ? slideIndex = slides.length : a > slides.length && (slideIndex = 1); for (let b = 0; b < slides.length; b++)slides[b].classList.remove("active"); for (let b = 0; b < dots.length; b++)dots[b].classList.remove("slider__control-button--active"); slides[slideIndex - 1].classList.add("active"), dots[slideIndex - 1].classList.add("slider__control-button--active") } function currentSlide(a) { showSlides(slideIndex = a) } dotsArea.onclick = function (a) { for (let b = 0; b < dots.length + 1; b++)a.target.classList.contains("slider__control-button") && a.target == dots[b - 1] && currentSlide(b) };